function Countdown(e,t,n,r,i){this.year=e;this.month=t-1;this.day=n;this.hour=r;this.minute=i;this.second=0;this.complete=false;this.id="countdown";this.dateFuture=new Date(this.year,this.month,this.day,this.hour,this.minute,this.second);this.setMessage=function(e){this.message=e};this.startCountdown=function(){var e=new Date;var t=this.dateFuture.getTime()-e.getTime();delete e;if(t<=0){this.complete=true;var n=document.getElementById(this.id);if(this.message){n.innerHTML='<p class="message">'+this.message+"</p>"}else{n.parentNode.removeChild(n)}}else{var r,i,s,o=0;t=Math.floor(t/1e3);r=Math.floor(t/86400);t=t%86400;i=Math.floor(t/3600);t=t%3600;s=Math.floor(t/60);t=t%60;o=Math.floor(t);document.getElementById("days").innerHTML=r;document.getElementById("hours").innerHTML=i;document.getElementById("mins").innerHTML=s;document.getElementById("secs").innerHTML=o;var u=this;setTimeout(function(){u.startCountdown()},1e3)}}}